
public class ifElsre {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 10;
		int y = 23;
		
		if (x == 10) {
			System.out.println("this is true");
		}
		else {
			System.out.println("this is false");
		}
		
		if (x != 10) {
			System.out.println("this is true");
		}
		else {
			System.out.println("this is false");
		}
		
		if (x == 10 && y == 24) {
			System.out.println("this is true");
		}
		else {
			System.out.println("this is false");
		}
		
		if (x == 10 || y == 23) {
			System.out.println("this is true");
		}
		else {
			System.out.println("this is false");
		}
		

	}

}
