
public class mathOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x, y, z, ans1, ans2, ans3, ans4, ans5;
		
		x = 20;
		y = 10;
		z = 8;
		
		ans1 = x + y;
		ans2 = x - y;
		ans3 = x * y;
		ans4 = x / y;
		ans5 = x % z;
		
		System.out.println(x + " + " + y + " = " + ans1);
		System.out.println(x + " - " + y + " = " + ans2);
		System.out.println(x + " * " + y + " = " + ans3);
		System.out.println(x + " / " + y + " = " + ans4);
		System.out.println(x + " % " + y + " = " + ans5);
		
		
		

	}

}
