
public final class loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 0;
		int b = 0;
		int c;
		
		//while loop
		while (a < 10)
		{
			System.out.println("the Beat goes on " + a);
			a++;
		}
		
		//do-while loop
		do
		{
			System.out.println("do the while " + b);
			b++;
		}while(b < 10);
		
		//for loop
		for(c = 0; c < 10; c++)
		{
			System.out.println("for the loop goes on " + c);
		}
	}

}
